actionConfiguration.body:
async () => {
  const orderProducts = await getOrderProducts.run();
  return orderProducts.map(p => {
    return {
      Id: p.id,
      Name: p.name,
      SKU: p.sku,
      Price: p.price,
      Quantity: p.quantity,
      Tax: p.taxes,
      Subtotal: parseInt(p.price) * parseInt(p.quantity),
      Image: p.image
    };
  });
}

