async () => {
  const orderTrack = await getOrderTrack.run();
  return orderTrack.map((o, index) => {
    return {
      id: index,
      Status: o.label,
      Date: new Date(o.created).toDateString(),
      Time: new Date(o.created).toLocaleTimeString().slice(0, 5)
    };
  });
}

